/** The stacks for our app are minimally defined here.  The internals of these
  * stacks aren't important, except that DatabaseStack exposes an attribute
  * "table" for a database table it defines, and ComputeStack accepts a reference
  * to this table in its properties.
  */
class DatabaseStack : Stack
{
    public Table Table { get; }

    public DatabaseStack(Construct scope, string id) : base(scope, id)
    {
        Table = new Table(this, "Table", new TableProps {
            PartitionKey = new Attribute { Name = "id", Type = AttributeType.STRING }
        });
    }
}

class ComputeProps
{
    public Table Table { get; set; }
}

class ComputeStack : Stack
{
    public ComputeStack(Construct scope, string id, ComputeProps props) : base(scope, id)
    {
    }
}

/**
 * Stack to hold the pipeline
 */
class MyPipelineStack : Stack
{
    public MyPipelineStack(Construct scope, string id, StackProps? props=null) : base(scope, id, props)
    {

        var pipeline = new CodePipeline(this, "Pipeline", new CodePipelineProps {
            Synth = new ShellStep("Synth", new ShellStepProps {
                // Use a connection created using the AWS console to authenticate to GitHub
                // Other sources are available.
                Input = CodePipelineSource.Connection("my-org/my-app", "main", new ConnectionSourceOptions {
                    ConnectionArn = "arn:aws:codestar-connections:us-east-1:222222222222:connection/7d2469ff-514a-4e4f-9003-5ca4a43cdc41"
                }),
                Commands = new [] { "npm ci", "npm run build", "npx cdk synth" }
            })
        });

        // 'MyApplication' is defined below. Call `addStage` as many times as
        // necessary with any account and region (may be different from the
        // pipeline's).
        pipeline.AddStage(new MyApplication(this, "Prod", new StageProps {
            Env = new Environment {
                Account = "123456789012",
                Region = "eu-west-1"
            }
        }));
    }
}

/**
 * Your application
 *
 * May consist of one or more Stacks (here, two)
 *
 * By declaring our DatabaseStack and our ComputeStack inside a Stage,
 * we make sure they are deployed together, or not at all.
 */
class MyApplication : Stage
{
    public MyApplication(Construct scope, string id, StageProps? props=null) : base(scope, id, props)
    {

        var dbStack = new DatabaseStack(this, "Database");
        new ComputeStack(this, "Compute", new ComputeProps {
            Table = dbStack.Table
        });
    }
}

// In your main file
// In your main file
new MyPipelineStack(this, "PipelineStack", new StackProps {
    Env = new Environment {
        Account = "123456789012",
        Region = "eu-west-1"
    }
});

java.source:
/** The stacks for our app are minimally defined here.  The internals of these
  * stacks aren't important, except that DatabaseStack exposes an attribute
  * "table" for a database table it defines, and ComputeStack accepts a reference
  * to this table in its properties.
  */
public class DatabaseStack extends Stack {
    public final Table table;

    public DatabaseStack(Construct scope, String id) {
        super(scope, id);
        this.table = Table.Builder.create(this, "Table")
                .partitionKey(Attribute.builder().name("id").type(AttributeType.STRING).build())
                .build();
    }
}

public class ComputeProps {
    private Table table;
    public Table getTable() {
        return this.table;
    }
    public ComputeProps table(Table table) {
        this.table = table;
        return this;
    }
}

public class ComputeStack extends Stack {
    public ComputeStack(Construct scope, String id, ComputeProps props) {
        super(scope, id);
    }
}

/**
 * Stack to hold the pipeline
 */
public class MyPipelineStack extends Stack {
    public MyPipelineStack(Construct scope, String id) {
        this(scope, id, null);
    }

    public MyPipelineStack(Construct scope, String id, StackProps props) {
        super(scope, id, props);

        CodePipeline pipeline = CodePipeline.Builder.create(this, "Pipeline")
                .synth(ShellStep.Builder.create("Synth")
                        // Use a connection created using the AWS console to authenticate to GitHub
                        // Other sources are available.
                        .input(CodePipelineSource.connection("my-org/my-app", "main", ConnectionSourceOptions.builder()
                                .connectionArn("arn:aws:codestar-connections:us-east-1:222222222222:connection/7d2469ff-514a-4e4f-9003-5ca4a43cdc41")
                                .build()))
                        .commands(List.of("npm ci", "npm run build", "npx cdk synth"))
                        .build())
                .build();

        // 'MyApplication' is defined below. Call `addStage` as many times as
        // necessary with any account and region (may be different from the
        // pipeline's).
        pipeline.addStage(MyApplication.Builder.create(this, "Prod")
                .env(Environment.builder()
                        .account("123456789012")
                        .region("eu-west-1")
                        .build())
                .build());
    }
}

/**
 * Your application
 *
 * May consist of one or more Stacks (here, two)
 *
 * By declaring our DatabaseStack and our ComputeStack inside a Stage,
 * we make sure they are deployed together, or not at all.
 */
public class MyApplication extends Stage {
    public MyApplication(Construct scope, String id) {
        this(scope, id, null);
    }

    public MyApplication(Construct scope, String id, StageProps props) {
        super(scope, id, props);

        DatabaseStack dbStack = new DatabaseStack(this, "Database");
        new ComputeStack(this, "Compute", new ComputeProps()
                .table(dbStack.getTable())
                );
    }
}

// In your main file
// In your main file
MyPipelineStack.Builder.create(this, "PipelineStack")
        .env(Environment.builder()
                .account("123456789012")
                .region("eu-west-1")
                .build())
        .build();

