class MyJenkinsStep(pipelines.Steppipelines.ICodePipelineActionFactory):
    def __init__(self, provider, input):
        super().__init__("MyJenkinsStep")

        # This is necessary if your step accepts parametres, like environment variables,
        # that may contain outputs from other steps. It doesn't matter what the
        # structure is, as long as it contains the values that may contain outputs.
        self.discover_referenced_outputs({
            "env": {}
        })

    def produce_action(self, stage, *, scope, actionName, runOrder, variablesNamespace=None, artifacts, fallbackArtifact=None, pipeline, codeBuildDefaults=None, beforeSelfMutation=None):

        # This is where you control what type of Action gets added to the
        # CodePipeline
        stage.add_action(cpactions.JenkinsAction(
            # Copy 'actionName' and 'runOrder' from the options
            action_name=action_name,
            run_order=run_order,

            # Jenkins-specific configuration
            type=cpactions.JenkinsActionType.TEST,
            jenkins_provider=self.provider,
            project_name="MyJenkinsProject",

            # Translate the FileSet into a codepipeline.Artifact
            inputs=[artifacts.to_code_pipeline(self.input)]
        ))

        return pipelines.CodePipelineActionFactoryResult(run_orders_consumed=1)

csharp.source:
class MyJenkinsStep : Step, ICodePipelineActionFactory
{
    public MyJenkinsStep(JenkinsProvider provider, FileSet input) : base("MyJenkinsStep")
    {

        // This is necessary if your step accepts parametres, like environment variables,
        // that may contain outputs from other steps. It doesn't matter what the
        // structure is, as long as it contains the values that may contain outputs.
        DiscoverReferencedOutputs(new Dictionary<string, IDictionary<string, object>> {
            { "env", new Struct { } }
        });
    }

    public CodePipelineActionFactoryResult ProduceAction(IStage stage, ProduceActionOptions options)
    {

        // This is where you control what type of Action gets added to the
        // CodePipeline
        stage.AddAction(new JenkinsAction(new JenkinsActionProps {
            // Copy 'actionName' and 'runOrder' from the options
            ActionName = options.ActionName,
            RunOrder = options.RunOrder,

            // Jenkins-specific configuration
            Type = JenkinsActionType.TEST,
            JenkinsProvider = Provider,
            ProjectName = "MyJenkinsProject",

            // Translate the FileSet into a codepipeline.Artifact
            Inputs = new [] { options.Artifacts.ToCodePipeline(Input) }
        }));

        return new CodePipelineActionFactoryResult { RunOrdersConsumed = 1 };
    }
}

java.source:
public class MyJenkinsStep extends Step implements ICodePipelineActionFactory {
    public MyJenkinsStep(JenkinsProvider provider, FileSet input) {
        super("MyJenkinsStep");

        // This is necessary if your step accepts parametres, like environment variables,
        // that may contain outputs from other steps. It doesn't matter what the
        // structure is, as long as it contains the values that may contain outputs.
        this.discoverReferencedOutputs(Map.of(
                "env", Map.of()));
    }

    public CodePipelineActionFactoryResult produceAction(IStage stage, ProduceActionOptions options) {

        // This is where you control what type of Action gets added to the
        // CodePipeline
        stage.addAction(JenkinsAction.Builder.create()
                // Copy 'actionName' and 'runOrder' from the options
                .actionName(options.getActionName())
                .runOrder(options.getRunOrder())

                // Jenkins-specific configuration
                .type(JenkinsActionType.TEST)
                .jenkinsProvider(this.provider)
                .projectName("MyJenkinsProject")

                // Translate the FileSet into a codepipeline.Artifact
                .inputs(List.of(options.artifacts.toCodePipeline(this.input)))
                .build());

        return CodePipelineActionFactoryResult.builder().runOrdersConsumed(1).build();
    }
}

